for i in $(seq 0 10 100); do for j in $(seq -90 20 90); do mysql --protocol=tcp -h127.0.0.1 -P4046 -uqsmaster -e "SELECT o1.source_id AS id1, o2.source_id AS id2, scisql_angSep(o1.ra,o1.decl,o2.ra,o2.decl) AS distance FROM wise_00.allwise_p3as_psd AS o1, wise_00.allwise_p3as_psd AS o2 WHERE scisql_s2PtInCircle(o1.ra,o1.decl,${i}.0,${j}.0,0.40)=1 AND scisql_angSep(o1.ra,o1.decl,o2.ra,o2.decl) < 0.016 AND o1.source_id <> o2.source_id" >& logs/0_${i}_${j}& done; done
