for i in $(seq 0 10 100); do for j in $(seq -90 20 90); do mysql --protocol=tcp -h127.0.0.1 -P4040 -uqsmaster -e "SELECT o1.source_id AS id1, o2.source_id AS id2, scisql_angSep(o1.ra,o1.dec,o2.ra,o2.dec) AS distance FROM gaia_dr2_02.gaia_source AS o1, gaia_dr2_02.gaia_source AS o2 WHERE scisql_s2PtInCircle(o1.ra,o1.dec,${i}.0,${j}.0,0.40)=1 AND scisql_angSep(o1.ra,o1.dec,o2.ra,o2.dec) < 0.016 AND o1.source_id <> o2.source_id" >& logs/0_${i}_${j}& done; done
