SELECT fs.forcedSourceId,fs.objectId,fs.ccdVisitId,fs.detect_isPrimary,fs.band,scisql_nanojanskyToAbMag(fs.psfFlux) AS psfMag,ccd.obsStartMJD,scisql_nanojanskyToAbMag(obj.r_psfFlux) AS obj_rpsfMag FROM dp02_dc2_catalogs.ForcedSource AS fs JOIN dp02_dc2_catalogs.CcdVisit AS ccd ON fs.ccdVisitId=ccd.ccdVisitId JOIN dp02_dc2_catalogs.Object AS obj ON fs.objectId=obj.objectId WHERE scisql_s2PtInCircle(obj.coord_ra,obj.coord_dec,62,-37,1.0)=1 AND obj.detect_isPrimary=1 AND obj.r_extendedness=0 AND scisql_nanojanskyToAbMag(obj.r_cModelFlux) > 17.5 AND scisql_nanojanskyToAbMag(obj.r_cModelFlux) < 18.0 AND fs.band='r'
