SELECT ts.ra,ts.dec,ts.flux_g,ts.flux_i,ts.flux_r,ts.id,ts.is_pointsource,ts.is_variable,ts.truth_type,ts.mag_r,obj.objectId,obj.coord_ra,obj.coord_dec,obj.g_psfFlux,obj.r_psfFlux,obj.i_psfFlux,obj.g_cModelFlux,obj.r_cModelFlux,obj.i_cModelFlux,obj.g_psfFluxErr,obj.r_psfFluxErr,obj.i_psfFluxErr,obj.g_cModelFluxErr,obj.r_cModelFluxErr,obj.i_cModelFluxErr,obj.tract,obj.patch,obj.g_extendedness,obj.r_extendedness,obj.i_extendedness,obj.g_blendedness,obj.r_blendedness,obj.i_blendedness,obj.detect_isPrimary FROM dp02_dc2_catalogs.MatchesTruth AS mt JOIN dp02_dc2_catalogs.TruthSummary AS ts ON ts.id_truth_type=mt.id_truth_type JOIN dp02_dc2_catalogs.Object AS obj ON mt.match_objectId=obj.objectId WHERE scisql_s2PtInCircle(ts.ra,ts.dec,64.5,-37.0,2.0)=1 AND ts.mag_r < 28
