SELECT coord_ra,coord_dec,scisql_nanojanskyToAbMag(g_calibFlux) AS g_calibMag,scisql_nanojanskyToAbMag(r_calibFlux) AS r_calibMag,scisql_nanojanskyToAbMag(i_calibFlux) AS i_calibMag,scisql_nanojanskyToAbMagSigma(g_calibFlux,g_calibFluxErr) AS g_calibMagErr FROM dp02_dc2_catalogs.Object WHERE scisql_s2PtInCircle(coord_ra,coord_dec,62,-37,1.0)=1 AND detect_isPrimary=1 AND g_calibFlux > 360 AND r_calibFlux > 360 AND i_calibFlux > 360 AND g_extendedness=0 AND r_extendedness=0 AND i_extendedness=0 LIMIT 10000
